import React from "react";
import "../App.css";
import Header from "./Header.js";
import Main from "./Main.js";
import Footer from "./Footer.js";
import ImagePopup from "./ImagePopup.js";
import api from "../utils/Api.js";
import CurrentUserContext from "../contexts/CurrentUserContext";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import DeletePopup from "./DeletePopup";
import { Navigate, Route, Routes } from "react-router-dom";
import Register from "./Register";
import Login from "./Login";
import ProtectedRouteElement from "./ProtectedRoute";
import * as auth from "./Auth";
import InfoTooltip from "./InfoTooltip";

function App() {
  const [isInfoTooltip, setInfoToolTipPopup] = React.useState(false);

  const [isSucces, setSuccess] = React.useState(false);

  const [loggedIn, setLoggedIn] = React.useState(false);

  const [cards, setCards] = React.useState([]);

  const [currentUser, setCurrentUser] = React.useState({});

  const [selectedCard, setSelectedCard] = React.useState(null);

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);

  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);

  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);

  const [deleteConfirm, setDeleteConfirm] = React.useState(null);

  function handleToolTipOpen() {
    setInfoToolTipPopup(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleAppPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function handleDeleteCard(card) {
    setDeleteConfirm(card);
  }

  function closeAllPopups() {
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setSelectedCard(null);
    setDeleteConfirm(null);
    setInfoToolTipPopup(false);
  }

  React.useEffect(() => {
    const jwt = localStorage.getItem("jwt");

    if (jwt) {
      setLoggedIn(true);
    }
  });

  function registerSubmit(email, password) {
    auth
      .register(email, password)
      .then((res) => {
        setLoggedIn(true);
        setSuccess(true);
        handleToolTipOpen();
      })
      .catch((err) => {
        console.error(err);
        setSuccess(false);
        handleToolTipOpen();
      });
  }

  function loginSubmit(email, password) {
    auth
      .login(email, password)
      .then((res) => {
        setLoggedIn(true);
        setSuccess(true);
        handleToolTipOpen();
      })
      .catch((err) => {
        console.error(err);
        setSuccess(false);
        handleToolTipOpen();
      });
  }

  function checkJTW() {}

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
    });
  }

  function handleCardDelete(id) {
    api
      .deleteCard(id)
      .then((card) => {
        setCards(() => cards.filter((c) => c._id !== id));
        closeAllPopups();
      })
      .catch((err) => console.error(err));
  }

  function handleSubmitUserForm(obj) {
    api
      .setNewUserInfo(obj)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.error(err));
  }

  function handleAvatarUserForm(avatar) {
    api
      .addNewAvatar(avatar)
      .then((res) => {
        setCurrentUser(res);
        closeAllPopups();
      })
      .catch((err) => console.error(err));
  }

  function handleAddPlaceSubmit(card) {
    api
      .addCard(card)
      .then((res) => {
        setCards([res, ...cards]);
        closeAllPopups();
      })
      .catch((err) => console.error(err));
  }

  React.useEffect(() => {
    Promise.all([api.getUserFromServer(), api.getInitialCards()])
      .then(([user, cardsList]) => {
        setCards(cardsList);
        setCurrentUser(user);
      })
      .catch((err) => console.error(err));
  }, []);

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={
              <ProtectedRouteElement
                element={Main}
                loggedIn={loggedIn}
                onEditProfile={handleEditProfileClick}
                onAddPlace={handleAppPlaceClick}
                onEditAvatar={handleEditAvatarClick}
                handleCardClick={handleCardClick}
                onCardLike={handleCardLike}
                cards={cards}
                onDelete={handleDeleteCard}
              />
            }
          />
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
        </Routes>
        {loggedIn ? <Header /> : ""}
        {loggedIn ? <Footer /> : ""}

        <EditProfilePopup
          onSubmit={handleSubmitUserForm}
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
        />

        <EditAvatarPopup
          onSubmit={handleAvatarUserForm}
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
        />

        <AddPlacePopup
          onSubmit={handleAddPlaceSubmit}
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
        />

        <ImagePopup onClose={closeAllPopups} card={selectedCard} />

        <DeletePopup
          onClose={closeAllPopups}
          card={deleteConfirm}
          onDelete={handleCardDelete}
        />
        <InfoTooltip
          isOpen={handleToolTipOpen}
          onClose={closeAllPopups}
          isSucces={isSucces}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
