import React from 'react'
import api from '../utils/Api.js'
function Main(props){

  const [userName, setUserName] = React.useState([]);
  const [userAvatar, setUserAvatar] = React.useState([]);
  const [userDescription, setUserDescription] = React.useState([]);
  const [cards, setCards] = React.useState([]);


React.useEffect(() => { 
api.getUserFromServer()
  .then((res) => { 
   setUserName(res.name);
   setUserDescription(res.about);
   setUserAvatar(res.avatar)
  }).catch((err) => console.error(err))
  }, []
)

React.useEffect(() => { 
  api.getInitialCards()
    .then((res) => { 
     setCards(res)
    })
    .then(cards.forEach((item) => { 
      <article className="grid-net__item">
          <div className="grid-net__item-image-box">
            <img className="grid-net__item-image" alt="просторы" style={{ backgroundImage: `url(${item.link})`}}/>
          </div>
          <button className="grid-net__item-button-delete" type="delete"></button>
          <div className="grid-net__item-box">
            <h2 className="grid-net__item-title">{item.name}</h2>
            <div className="grid-net__item-user-box">
              <button className="grid-net__item-button"></button>
              <span className="grid-net__item-number">{item.likes.length}</span>
            </div>
          </div>
        </article>
        gridNet.append(item)
    }
    )
  ).catch((err) => console.error(err))
    }, []
  )

  console.log(cards)
  const gridNet = document.querySelector('.grid-net')
  console.log(gridNet) 

    return ( 
        <main className="content">
      <section className="profile" aria-label="Профиль">
        <div className="profile__avatar-area">
          <img src='' className="profile__avatar" alt="аватар-профиля" style={{ backgroundImage: `url(${userAvatar})` }}/>
          <button className="profile__avatar-area-button"  onClick={props.onEditAvatar} type="button"></button>
        </div>
        <div className="profile__info">
          <div className="profile__info-box">
            <h1 className="profile__info-fullname">{userName}</h1>
            <button className="profile__info-button" onClick={props.onEditProfile} type="button"></button>
          </div>
          <p className="profile__info-occupation">{userDescription}</p>
        </div>
        <button className="profile__button" onClick={props.onAddPlace} type="button"></button>
      </section>
      <section className="grid-net" aria-label="грид-сетка"></section>
    </main>
    )
}

export default Main
