import React from 'react'
import '../App.css'
import Header from './Header.js'
import Main from './Main.js'
import Footer from './Footer.js'
import PopupWithForm from './PopupWithForm.js'
import Api from "../utils/Api.js"

function App() {
  function handleEditAvatarClick(){ 
    const avatarPopup = document.querySelector('.popup_type_profile-avatar');
    avatarPopup.classList.add('popup_active')
    setIsEditAvatarPopupOpen(true)
};
function handleEditProfileClick(){ 
    const profilePopup = document.querySelector('.popup_type_profile');
    profilePopup.classList.add('popup_active')
    setIsEditProfilePopupOpen(true)
};
function handleAppPlaceClick(){ 
    const cardPopup = document.querySelector('.popup_type_create-card');
    cardPopup.classList.add('popup_active')
    setIsAddPlacePopupOpen(true)
};

const api = new Api({
  baseUrl: "https://mesto.nomoreparties.co/v1/cohort-72",
  headers: {
    authorization: "ee9de7f6-7aed-4e20-b5bf-f52e55007fe4",
    "Content-type": "application/json",
  },
});

function closeAllPopups(){
  setIsAddPlacePopupOpen(false)
  setIsEditAvatarPopupOpen(false)
  setIsEditProfilePopupOpen(false)
}

  const [selectedCard, setSelectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);

React.useEffect(() => {
  Promise.all([api.getUserFromServer(), api.getInitialCards()])
  .then(([userInfo, cards]) => {
    setCurrentUser.setUserInfo(userInfo);
    setCurrentUser.setUserAvatar(userInfo)
    setCards(cards)
  })
  .catch((err) => {
    console.error(err);
  });
})

const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false)
const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false)

  return (
    <div className="App">
      <Header />
      <Main 
      onEditProfile={handleEditProfileClick}
       onAddPlace={handleAppPlaceClick}
        onEditAvatar={handleEditAvatarClick}
       />
      <Footer />
      <PopupWithForm 
      isOpen={isEditProfilePopupOpen}
      name={`profile`}
      title={`Редактировать профиль`}
      button={`Сохранить`}
      onClose={closeAllPopups}
     ><label className="popup__field">
     <input
       className="popup__input popup__input_type_username"
       id="username"
       name="username"
       type="text"
       placeholder="Имя"
       required
       minlength="2"
       maxlength="40"
     />
     <span className="username-error popup__input-error"></span>
   </label>
   <label className="popup__field">
     <input
       className="popup__input popup__input_type_occupation"
       id="occupation"
       name="occupation"
       type="text"
       placeholder="О себе"
       required
       minlength="2"
       maxlength="200"
     />
     <span className="occupation-error popup__input-error"></span>
   </label></PopupWithForm>
<PopupWithForm
 onClose={closeAllPopups}
 isOpen={isAddPlacePopupOpen}
 name={`create-card`}
 title={`Новое место`}
 button={`Сохранить`}>
  <label className="popup__field">
            <input
              className="popup__input popup__input_type_place-name"
              id="name"
              name="name"
              type="text"
              placeholder="Название"
              required
              minlength="2"
              maxlength="30"
            />
            <span className="name-error popup__input-error"></span>
          </label>
          <label className="popup__field">
            <input
              className="popup__input popup__input_type_link"
              id="link"
              name="link"
              type="url"
              placeholder="Ссылка на картинку"
              required
            />
            <span className="link-error popup__input-error"></span>
          </label>
 </PopupWithForm>
<PopupWithForm 
isOpen={isEditAvatarPopupOpen}
onClose={closeAllPopups}
name={`profile-avatar`}
title={`Обновить аватар`}
button={`Сохранить`}>
   <label className="popup__field">
            <input
              className="popup__input popup__input_type_avatar"
              name="avatar"
              id="avatar"
              minlength="5"
              maxlength="200"
              type="url"
              required
              placeholder="Ссылка"
            />
            <span className="avatar-error popup__input-error"></span>
          </label>
</PopupWithForm>

    <template id="template-card">
      <article className="grid-net__item">
        <div className="grid-net__item-image-box">
          <img className="grid-net__item-image" alt="просторы" />
        </div>
        <button className="grid-net__item-button-delete" type="delete"></button>
        <div className="grid-net__item-box">
          <h2 className="grid-net__item-title"></h2>
          <div className="grid-net__item-user-box">
            <button className="grid-net__item-button"></button>
            <span className="grid-net__item-number"></span>
          </div>
        </div>
      </article>
    </template>

    <div className="popup popup_type_image-overlay">
      <div className="popup__overlay-holder">
        <img className="popup__image" />
        <p className="popup__span"></p>
        <button className="popup__close-button"></button>
      </div>
    </div>

    <div className="popup popup_type_delete-card">
      <div className="popup__container popup__container_type-delete">
        <h2 className="popup__title popup__title_type_delete-card">Вы уверены ?</h2>
        <button className="popup__close-button" type="button"></button>
        <button className="popup__submit popup__submit_type-confirm" type="button">
          Да
        </button>
      </div>
    </div>
      </div>
  );
}

export default App;
